import { Component, Inject, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';
import { MAT_DIALOG_DATA } from '@angular/material/dialog';
import { map, Observable, startWith } from 'rxjs';
import { ApiService } from 'src/app/services/api.service';
import { DialogData } from '../decision.component';

export interface Participant {
  name: string;
}

@Component({
  selector: 'participants-dialog-component',
  templateUrl: './participants-dialog.component.html',
  styleUrls: ['./participants-dialog.component.scss'],
})
export class ParticipantsDialogComponent implements OnInit {
  constructor(
    @Inject(MAT_DIALOG_DATA) public data: DialogData,
    private api: ApiService
  ) {}

  myControl = new FormControl<string | Participant>('');
  options: Participant[] = [
    { name: 'John Stuart' },
    { name: 'Jeff Bezos' },
    { name: 'Igor Lound' },
  ];
  filteredOptions?: Observable<Participant[]>;

  ngOnInit() {
    this.filteredOptions = this.myControl.valueChanges.pipe(
      startWith(''),
      map((value) => {
        const name = typeof value === 'string' ? value : value?.name;
        return name ? this._filter(name as string) : this.options.slice();
      })
    );
  }

  private _filter(name: string): Participant[] {
    const filterValue = name.toLowerCase();

    return this.options.filter((option) =>
      option.name.toLowerCase().includes(filterValue)
    );
  }

  addParticipant() {
    console.log();
  }
}
<div mat-dialog-content class="mat-dialog">
  <p class="title">Insert a participant from your organization:</p>
  <mat-form-field class="participants-full-width" appearance="fill">
    <mat-label>Available participants</mat-label>
    <input
      type="text"
      matInput
      [formControl]="myControl"
      [matAutocomplete]="auto"
    />
    <mat-autocomplete #auto="matAutocomplete">
      <mat-option
        *ngFor="let option of filteredOptions | async"
        [value]="option"
      >
        {{ option.name }}
      </mat-option>
    </mat-autocomplete>
  </mat-form-field>
</div>

<div class="mat-dialog-actions">
  <div><button mat-raised-button mat-dialog-close>Close</button></div>
  <div>
    <button mat-raised-button [mat-dialog-close]="data.name" cdkFocusInitial>
      Ok
    </button>
  </div>
</div>
