import { HttpClient } from '@angular/common/http';
import { Injectable, OnInit } from '@angular/core';
import { catchError } from 'rxjs';

const baseUrl = 'http://localhost:8080/participants';

@Injectable({
  providedIn: 'root',
})
export class ApiService implements OnInit {
  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.postParticipant('Jo');
  }

  async postParticipant(data: string) {
    (async () => {
      try {
        console.log('participante', data);
        this.http.post<any>(baseUrl, data).toPromise();
      } catch (e) {
        console.log('caught error', e);
      }
    })();
  }

  getParticipants() {
    return this.http.get<any>(baseUrl);
  }

  getSpecificParticipant(data: number) {
    return this.http.get<any>(`${baseUrl}/${data}`);
  }
}
