import { Component, Inject, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { Observable, startWith, map } from 'rxjs';
import { ApiService } from 'src/app/services/api.service';
import { DialogData } from '../decision.component';

@Component({
  selector: 'participants-dialog-component',
  templateUrl: './participants-dialog.component.html',
  styleUrls: ['./participants-dialog.component.scss'],
})
export class ParticipantsDialogComponent implements OnInit {
  constructor(
    public dialogRef: MatDialogRef<ParticipantsDialogComponent>,
    @Inject(MAT_DIALOG_DATA) public data: DialogData,
    private service: ApiService
  ) {}

  myControl = new FormControl<string | DialogData>('');
  options: any;
  filteredOptions?: Observable<DialogData[]>;

  ngOnInit() {
    this.options = this.service
      .getParticipants()
      .subscribe((res) => console.log(res));

    this.filteredOptions = this.myControl.valueChanges.pipe(
      startWith(''),
      map((value) => {
        const name = typeof value === 'string' ? value : value?.name;
        return name ? this._filter(name as string) : this.options;
      })
    );
  }

  displayFn(participantName: DialogData): string {
    return participantName && participantName.name ? participantName.name : '';
  }

  private _filter(name: string): DialogData[] {
    const filterValue = name.toLowerCase();

    return this.options.filter((option: any) =>
      option.name.toLowerCase().includes(filterValue)
    );
  }

  postParticipant(participantName: any) {
    this.service
      .postParticipant(participantName)
      .subscribe((res) => console.log(res));
    this.service.getParticipants().subscribe((res) => console.log(res));
  }

  onCancel(): void {
    this.dialogRef.close();
  }
}
<div mat-dialog-content class="mat-dialog">
  <p class="title">Insert a participant from your organization:</p>
  <mat-form-field class="participants-full-width" appearance="fill">
    <mat-label>Available participants</mat-label>
    <input
      type="text"
      matInput
      [formControl]="myControl"
      [matAutocomplete]="auto"
      #input
    />
    <mat-autocomplete #auto="matAutocomplete" [displayWith]="displayFn">
      <mat-option
        *ngFor="let option of filteredOptions | async"
        [value]="option"
      >
        {{ option.name }}
      </mat-option>
    </mat-autocomplete>
  </mat-form-field>
</div>

<div class="mat-dialog-actions">
  <div>
    <button mat-raised-button (click)="onCancel()" mat-dialog-close>
      Close
    </button>
  </div>
  <div>
    <button
      mat-raised-button
      [mat-dialog-close]="data.name"
      cdkFocusInitial
      (click)="postParticipant(input.value)"
    >
      Ok
    </button>
  </div>
</div>
